const additionHtmlAvoidJumpLine = (target, source) => {
    if(target.length > 0 && target.endsWith("</p>") && source.startsWith("<p>")) {
        return target.substring(0, target.length - ("</p>".length)) + source.substring("<p>".length, source.length);
    } else {
        return target + source;
    }
}

